/*global sessionStorage, window, jQuery*/
/*global checkTarget*/

/* REFRESH PAGE */
function refresh() { // eslint-disable-line no-unused-vars
    'use strict';
    window.location.reload(false);
    return false;
}

/* SEND TEXT NOTIFICATION TO USER */
function adjustNotification(text) { // eslint-disable-line no-unused-vars
    'use strict';
    jQuery("#notifications").html("");
    jQuery("#notifications").html(text);
}

/* STORE TARGET INPUTED BY USER */
function storeTarget(input) { // eslint-disable-line no-unused-vars
    'use strict';
    
    if (input.value !== "") {
        sessionStorage.setItem(input.name, input.value); //STORE INPUT VALUE FOR LATER
    } else {
        sessionStorage.setItem(input.name, "0"); //NO TARGET ENTERED
    }
            
    var heatValue, lengthValue;
    heatValue = sessionStorage.getItem("heatOutput");
    lengthValue = sessionStorage.getItem("totalLength");
        
    //CHECK IF INPUT IS EMPTY
    if ((heatValue === "0" || heatValue === "") && (lengthValue === "0" || lengthValue === "")) {
            
        adjustNotification("Continue Without Target");
        checkTarget();
            
    } else {

        adjustNotification("Target-Entered");
        checkTarget();
            
    }
    
}